'use strict';

var filter = require('lodash/filter');
var curry = require('lodash/curry');

var _require = require('../helpers/dates'),
    isDateBefore = _require.isDateBefore,
    isDateAfter = _require.isDateAfter;

function brewedBeforeFilter(val, db) {
  if (val == null) return db;
  return filter(db, function (b) {
    return isDateBefore(b.first_brewed, val);
  });
}

function brewedAfterFilter(val, db) {
  if (val == null) return db;
  return filter(db, function (b) {
    return isDateAfter(b.first_brewed, val);
  });
}

exports.brewedBeforeFilter = curry(brewedBeforeFilter);
exports.brewedAfterFilter = curry(brewedAfterFilter);