'use strict';

var filter = require('lodash/filter');
var curry = require('lodash/curry');

var _require = require('../helpers/stringMatch'),
    stringMatch = _require.stringMatch;

function foodFilter(val, db) {
  if (val == null) return db;
  return filter(db, function (b) {
    return b.food_pairing.some(function (o) {
      return stringMatch(o, val);
    });
  });
}

module.exports = curry(foodFilter);