'use strict';

var moment = require('moment');

var isDateBefore = function isDateBefore(brewDate, predicate) {
  var parsedBrewDate = moment(brewDate, 'MM/YYYY');
  var parsedPredicate = moment(predicate, 'MM-YYYY');

  if (!parsedPredicate.isValid()) return false;

  return parsedBrewDate.isBefore(parsedPredicate);
};

var isDateAfter = function isDateAfter(brewDate, predicate) {
  var parsedBrewDate = moment(brewDate, 'MM/YYYY');
  var parsedPredicate = moment(predicate, 'MM-YYYY');

  if (!parsedPredicate.isValid()) return false;

  return parsedBrewDate.isAfter(parsedPredicate);
};

exports.isDateAfter = isDateAfter;
exports.isDateBefore = isDateBefore;